/** @jsxImportSource @emotion/react */
import styled from "@emotion/styled";
import AboutButton from "../components/AboutButton";
import Dune from "../images/dune.jpg";
import Triangles from "../images/levels.svg"
import React from "react";
import "./home.scss";
import CircleButton from "../components/CircleButton";
import ReactPageScroller from "react-page-scroller";
import AboutPage from "./About";
import "./home.scss";

const tablet = `@media (max-width: 800px)`;
const mobile = `@media (max-width: 500px)`;

const HomeBackground = styled.div`
  background: url(${Dune}) no-repeat center center fixed;
  background-size: cover;
  height: 100%;
  font-size: 24px;
  color: white;
  font-weight: bold;
  bottom: 0;
`;

const HomeDiv = styled.div`
  top: 350px;
  width: 350px;
  font-size: 40px;
  padding: 10px;
  border-style: solid;
  border-width: 2px;
  border-color: #aeff00;
  z-index: 4000;
  left: 120px;
  position: relative;
  font-family: Good-Times-Regular;
  letter-spacing: 4px;
`;

const DuneImg = styled.img`
  width: 85%;
  top: 50px;
  right: 100px;
  position: absolute;
  border-right: 6px solid #aeff00;
  border-bottom: 6px solid #aeff00;
  ${tablet} {
    padding: 60px 40px;
  }
  ${mobile} {
    padding: 80px 0 80px 130px;
  }
`;

const Figure = styled.p`
  position: relative;
  z-index: 4000;
  left: 1300px;
  top: 580px;
  font-weight: lighter;
  letter-spacing: 2px;
  font-family: Kanit-Thin-Italic;
`;

const Scroll = styled.p`
  color: white;
  position: absolute;
  left: 10px;
  bottom: 50px;
  letter-spacing: 2px;
  transform: rotate(-90deg);
  font-family: Kanit-Thin-Italic;
`;

const Box = styled.div`
  margin-left: 20px;
  height: 20px;
  width: 20px;
  clear: both;
`;

function Home() {
  return (
    <ReactPageScroller animationTimer={700}>
    <HomeBackground>
        <div>
          <HomeDiv> JOHN SCHRIEMER </HomeDiv>
          <AboutButton />
          <Box style={{ backgroundColor: "#aeff00" }} />
          <Box style={{ backgroundColor: "#C2FF40" }} />
          <Box style={{ backgroundColor: "#D8FF84" }} />
          <Box style={{ backgroundColor: "#F2FFD5" }} />
          <DuneImg src={Dune} />
          <Figure>EXP.0001 - DUNES</Figure>
          <Scroll>&#8592; SCROLL</Scroll>
          <CircleButton />
          <img src={Triangles} style={{ color: "red", width: "50px"}}/>
          <img src={Triangles} style={{width: "50px"}}/>
          <img src={Triangles} style={{width: "50px"}}/>
          <img src={Triangles} style={{width: "50px"}}/>
        </div>
    </HomeBackground>
    <div>
          <AboutPage />
        </div>
    </ReactPageScroller>
  );
}

export default Home;
