/** @jsxImportSource @emotion/react */
import styled from "@emotion/styled";
import React, { useRef, useEffect, useState } from "react";
import { Footer } from "../components/Footer";
import { Parallax, ParallaxLayer, IParallax } from "@react-spring/parallax";
import flowerBackground from "../images/flowers.png";
import { Interpolation, Theme, ThemeProvider, useTheme } from "@emotion/react";
import { JSX } from "@emotion/react/jsx-runtime";

// eslint-disable-next-line @typescript-eslint/no-unused-vars
const tablet = `@media (max-width: 800px)`;
const mobile = `@media (max-width: 500px)`;

// outside of the component
interface Themes {
  colors: {
    primary: string;
    background: string;
  };
}

const lightTheme = {
  colors: {
    primary: "red",
    background: "white",
  },
};

const darkTheme = {
  colors: {
    primary: "blue",
    background: "black",
  },
};

const PrimaryText = styled.div<Themes>`
  padding: 20,
  color: ${(props) => props.colors.primary},
  backgroundColor:  ${(props) => props.colors.background},
`;

function LandingPage() {
  const parallax = useRef<IParallax>(null!);
  const [theme, setTheme] = useState(lightTheme);
  const [themeName, setThemeName] = useState(Object.keys(theme)[0]);

  const [isMounted, setIsMounted] = useState(false);

  console.log(theme)

  const handleClick = () => {
    // toggle theme
    setTheme(theme === lightTheme ? darkTheme : lightTheme);
    setThemeName(theme === lightTheme ? "darkTheme" : "lightTheme");
  };

  useEffect(() => {
    setIsMounted(true);
  }, []);

  return (
    <>
      {isMounted && (
        <ThemeProvider theme={theme}>
          <div style={{ display: "flex", width: "100%", marginLeft: "-8px" }}>
            {!mobile ? (
              <div>
                <div style={{ display: "flex", paddingBottom: "20px" }}>
                  <p>hello</p>
                </div>
              </div>
            ) : (
              <div style={{ display: "flex" }}>
                <Parallax ref={parallax} pages={3}>
                  <ParallaxLayer
                    offset={2}
                    speed={1}
                    style={{ backgroundColor: "#5E8062", width: "100%" }}
                  >
                    <Footer />
                    <p>yessir</p>
                  </ParallaxLayer>
                  <ParallaxLayer
                    offset={1}
                    speed={1}
                    style={{ backgroundColor: theme.colors.primary }}
                  >
                    <div style={{ display: "flex", paddingBottom: "20px" }}>
                      <p>hello</p>
                    </div>
                  </ParallaxLayer>
                  <ParallaxLayer
                    offset={0}
                    speed={0}
                    factor={3}
                    style={{
                      backgroundImage: flowerBackground,
                      backgroundSize: "cover",
                    }}
                  >
                    <div style={{ display: "flex", paddingBottom: "20px" }}>
                      <p>goodbye</p>
                      <PrimaryText colors={theme.colors}>
                        This text is the primary color
                      </PrimaryText>
                      <button onClick={handleClick}>
                        Change to {themeName} mode
                      </button>
                    </div>
                  </ParallaxLayer>
                </Parallax>
              </div>
            )}
          </div>
        </ThemeProvider>
      )}
    </>
  );
}

export default LandingPage;
