/** @jsxImportSource @emotion/react */
import styled from "@emotion/styled";
import React from "react";
import Star from "../images/star.svg";
import Ball from "../images/ball.png";
import BlackDisk from "../images/diskBlack.png"
import AboutIcons from "../components/AboutIcons";
import { Footer } from "../components/Footer";

const mobile = `@media (max-width: 500px)`;

const AboutDiv = styled.div`
  width: 100%;
  height: 100%;
  min-height: 100%;
  font-size: 24px;
  color: white;
  margin: 0 auto;
  bottom: 0;
`;

const AboutText = styled.p`
  padding: 30px;
  max-width: 640px;
  letter-spacing: 2px;
  font-family: Kanit-Medium;
  font-size: 20px;
  color: white;
`;

const StarImg1 = styled.img`
  width: 100px;
  position: absolute;
  color: "#aeff00";
  padding: 40px;
  ${mobile} {
    width: 40px;
    top: 160px;
  }
`;

const StarImg2 = styled.img`
  width: 100px;
  position: absolute;
  right: 0;
  color: "#aeff00";
  padding: 40px;
  ${mobile} {
    width: 40px;
    top: -20px;
    right: 40px;
  }
`;

const DiskBlackImg = styled.img`
  position: absolute;
  width: 100%;
  padding: 10px;
`;

const AboutIconsContainer = styled.div`
  padding: 30px;
  text-align: center;
`;

const AboutContainer = styled.div`
  display: flex;
  position: fixed;
  top: 120%;
  left: 12%;

  ${mobile} {
    left: -50px;
  }
`;

function About() {
  return (
    <AboutDiv className="About">
      <DiskBlackImg src={BlackDisk} />
      <br/>
      <AboutContainer>
        <AboutIconsContainer>
          <AboutIcons />
        </AboutIconsContainer>
        <AboutText></AboutText>
      </AboutContainer>
      <StarImg1 src={Star} />
      <StarImg2 src={Star} />
      <Footer />
    </AboutDiv>
  );
}

export default About;
