/** @jsxImportSource @emotion/react */
import styled from "@emotion/styled";
import React, { useRef, useEffect, useState } from "react";
import { Footer } from "../components/Footer";
import { Parallax, ParallaxLayer, IParallax } from "@react-spring/parallax";
import flowerBackground from "../images/flowers.png";
import { ThemeProvider, Theme } from "@emotion/react";

// eslint-disable-next-line @typescript-eslint/no-unused-vars
const tablet = `@media (max-width: 800px)`;
const mobile = `@media (max-width: 500px)`;

const lightTheme = {
  colors: {
    primary: "deeppink",
    background: "white",
  },
};

const darkTheme = {
  colors: {
    primary: "lightpink",
    background: "black",
  },
};

const PrimaryText = styled.div`
  padding: 20,
  color: ${theme.colors.primary};
  backgroundColor:  ${theme.colors.background},
`;

function ThemeToggler({ theme, onClick }) {
  const nextTheme = theme === "light" ? "dark" : "light";

  return (
    <button onClick={() => onClick(nextTheme)}>
      Change to {nextTheme} mode
    </button>
  );
}

function LandingPage() {
  const parallax = useRef<IParallax>(null!);
  const [theme, setTheme] = useState(lightTheme);
  const [themeName, setThemeName] = useState(Object.values(theme)[0]);

  const [isMounted, setIsMounted] = useState(false);

  const handleClick = () => {
    // toggle theme
    setTheme(theme === lightTheme ? darkTheme : lightTheme);
  };

  useEffect(() => {
    setIsMounted(true);
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <div style={{ display: "flex", width: "100%", marginLeft: "-8px" }}>
        {!mobile ? (
          <div>
            <div style={{ display: "flex", paddingBottom: "20px" }}>
              <p>hello</p>
            </div>
          </div>
        ) : (
          <div style={{ display: "flex" }}>
            <Parallax ref={parallax} pages={3}>
              <ParallaxLayer
                offset={2}
                speed={1}
                style={{ backgroundColor: "#5E8062", width: "100%" }}
              >
                <Footer />
                <p>yessir</p>
              </ParallaxLayer>
              <ParallaxLayer
                offset={1}
                speed={1}
                style={{ backgroundColor: theme.colors.primary }}
              >
                <div style={{ display: "flex", paddingBottom: "20px" }}>
                  <p>hello</p>
                </div>
              </ParallaxLayer>
              <ParallaxLayer
                offset={0}
                speed={0}
                factor={3}
                style={{
                  backgroundImage: flowerBackground,
                  backgroundSize: "cover",
                }}
              >
                <div style={{ display: "flex", paddingBottom: "20px" }}>
                  <p>goodbye</p>
                  <PrimaryText>This text is the primary color</PrimaryText>
                  <ThemeToggler theme={theme} onClick={handleClick} />
                </div>
              </ParallaxLayer>
            </Parallax>
          </div>
        )}
      </div>
    </ThemeProvider>
  );
}

export default LandingPage;
