/** @jsxImportSource @emotion/react */
import styled from "@emotion/styled";
import { Link } from "react-router-dom";
import React, { useState } from "react";
import "../pages/home.scss";
import { useTrail, a } from "@react-spring/web";

const tablet = `@media (max-width: 800px)`;
const mobile = `@media (max-width: 500px)`;

const StyledAboutButton = styled.button`
  background-color: transparent;
  border: none;
  font-family: halyard-display, sans-serif;
  letter-spacing: 1px;
  text-indent: 1px;
  text-transform: uppercase;
  color: #aeff00;
  border: none;
	padding: 0;
	background: none;
	transform: rotate(-90deg);
	font-size: 2em;
	position: absolute;
	right: 50px;
  top: 170px;
  z-index: 4000;

  ${mobile} {
    font-size: 1.7em;
    top: 420px;
    right: -30px;
  }
}
`;

const AboutTextContainer = styled.div`
  background-color: transparent;
  border: none;
  font-family: halyard-display, sans-serif;
  color: white;
  border: none;
	padding: 0;
	background: none;
	font-size: 1.4em;
	position: absolute;
	right: 300px;
  top: 280px;
  z-index: 4000;

  ${mobile} {
    font-size: 1.0em;
    top: 240px;
    right: 80px;
  }
  }
`;

const Trail: React.FC<{ open: boolean }> = ({ open, children }) => {
  const items = React.Children.toArray(children);
  const trail = useTrail(items.length, {
    config: { mass: 41, tension: 2000, friction: 400 },
    opacity: open ? 1 : 0,
    x: open ? 0 : 20,
    height: open ? 50 : 0,
    from: { opacity: 0, x: 20, height: 0 },
  });
  return (
    <div>
      {trail.map(({ height, ...style }, index) => (
        <a.div key={index} style={style}>
          <a.div style={{ height }}>{items[index]}</a.div>
        </a.div>
      ))}
    </div>
  );
};

function AboutButton() {
  const [open, set] = useState(false);
  return (
    <div>
      <StyledAboutButton onClick={() => set((state) => !state)}>
        <div className="link link--helike">
          <span>About</span>
        </div>
      </StyledAboutButton>
      <AboutTextContainer>
        <Trail open={open}>
          <span>&lt; p &gt;</span>
          <span> Hello! I'm John, a </span>
          <span>fullstack developer</span>
          <span>based out of Vancouver. </span>
          <span>I design user interfaces </span>
          <span>and bring them to life!</span>
          <span>&lt; / p &gt;</span>
        </Trail>
      </AboutTextContainer>
    </div>
  );
}

export default AboutButton;
