/** @jsxImportSource @emotion/react */
import { useState } from "react";
import styled from "@emotion/styled";
import React from "react";
import AboutImg from "../images/about.png";
import Rhombus from "../images/rhombus.svg";

const AboutDiv = styled.div`
  background: url(${AboutImg}) no-repeat;
  bottom: 0;
  z-index: 8000;
`;

const AboutText = styled.div`
  max-width: 600px;
`;

const Scroll = styled.p`
  color: red;
  position: absolute;
	left: 10px;
	bottom: 50px;
  letter-spacing: 2px;
  transform: rotate(-90deg);
  font-family: Kanit-Thin-Italic;
`;

function About() {
  return (
    <AboutDiv className="About">
      <AboutText>
        <h2>Hi there!</h2>
        <br />
        <p>
          My name is John and I am a fullstack developer based out of beautiful
          British Columbia. I design user interfaces and bring them to life
          using modern frameworks and tools. I'd love to get in touch!
        </p>
        <br />
        <p>hello@johnschriemer.com</p>
      </AboutText>
      <img src={Rhombus}/>
      <Scroll>&#8592; SCROLL</Scroll>
    </AboutDiv>
  );
}

export default About;
