/** @jsxImportSource @emotion/react */
import { useState } from "react";
import styled from "@emotion/styled";
import React from "react";
import AboutImg from "../images/about.png";
import Rhombus from "../images/rhombus.svg";
import GitHubButton from "react-github-btn";
import { FaGithub, FaLinkedinIn, FaMediumM, FaInstagram } from "react-icons/fa";

const AboutDiv = styled.div`
  background-image: url(${AboutImg});
  background-size: cover;
  z-index: 8000;
  width: 100%;
  height: 100%;
`;

const AboutText = styled.p`
  padding: 30px;
  max-width: 600px;
  font-family: Kanit-Regular;
  font-size: 20px;
  color: white;
`;

const StyledIconHeader = styled.p`
  color: #aeff00;
  font-family: Kanit-thin;
  transform: rotate(-90deg);
`;

function About() {
  return (
    <AboutDiv className="About">
      <AboutText>
        My name is John and I am a fullstack developer based out of beautiful
        British Columbia. I design user interfaces and bring them to life using
        modern frameworks and tools.
      </AboutText>
      <div style={{ display: "flex" }}>
        <div style={{color: "#aeff00"}}>
        <StyledIconHeader>LinkedIn</StyledIconHeader>
        WORK EXPERIENCE
          <FaLinkedinIn />
        </div>
        <div>
        <StyledIconHeader>Github</StyledIconHeader>
        Side Projects
          <FaGithub />
        </div>
        <div>
        <StyledIconHeader>Medium</StyledIconHeader>
        Blog
          <FaMediumM />
        </div>
        <div>
        <StyledIconHeader>Instagram</StyledIconHeader>
        Personal
          <FaInstagram />
        </div>
      </div>
      <GitHubButton
        href="https://github.com/jschriemer/jschriemer.github.io"
        data-color-scheme="no-preference: dark_dimmed; light: dark; dark: dark_dimmed;"
        data-size="large"
        data-show-count="true"
        aria-label="Star jschriemer/jschriemer.github.io on GitHub"
      >
        Star
      </GitHubButton>
      <div style={{bottom: 0}}>
      <img
        src={Rhombus}
        style={{ width: "50px", transform: "rotate(-50deg)" }}
      />
      <img
        src={Rhombus}
        style={{ width: "50px", transform: "rotate(-50deg)" }}
      />
      <img
        src={Rhombus}
        style={{ width: "50px", transform: "rotate(-50deg)" }}
      />
      </div>
      {/*<Scroll>&#8592; SCROLL</Scroll>*/}
    </AboutDiv>
  );
}

export default About;
